import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ForgotPassword from "./components/ForgotPassword";
import ResetPassword from "./components/ResetPassword";
import LoadingModal from "./components/LoadingModal"; // Import LoadingModal component
import Home from "./components/pages/Home";
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute

// Function to check if the user is authenticated (from the backend)
const checkAuth = async () => {
  try {
    const token = localStorage.getItem("authToken");

    if (!token) {
      console.log("No token found");
      return false; // If no token is found, assume the user is not authenticated
    }

    const response = await fetch(
      "http://localhost:5000/api/auth/verify-token",
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );

    if (!response.ok) {
      throw new Error("Authentication failed");
    }

    const data = await response.json();
    return data.success; // If the token is valid, return true
  } catch (error) {
    console.error("Error verifying token:", error);
    return false; // If there's an error, assume the user is not authenticated
  }
};

function App() {
  const [isLoading, setIsLoading] = useState(true); // State to control loading modal
  const [isAuthenticated, setIsAuthenticated] = useState(false); // State to control authentication status

  useEffect(() => {
    const checkUserAuth = async () => {
      const isAuth = await checkAuth(); // Call the backend to check authentication status
      setIsAuthenticated(isAuth); // Update the authentication state based on the response
      setIsLoading(false); // Hide loading modal once the check is complete
    };

    checkUserAuth(); // Check authentication when the component mounts
  }, []);

  return (
    <Router>
      {/* Show the LoadingModal while loading */}
      <LoadingModal isVisible={isLoading} />

      <Routes>
        {/* Wrap protected routes with ProtectedRoute */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/reset-password" element={<ResetPassword />} />
      </Routes>
    </Router>
  );
}

export default App;
