// App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ForgotPassword from "./components/ForgotPassword";
import ResetPassword from "./components/ResetPassword";
import Home from "./components/Home"; // Import Home component
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute component
import LoadingModal from "./components/LoadingModal"; // Import LoadingModal component

function App() {
  const [isLoading, setIsLoading] = useState(true); // State to control loading modal

  useEffect(() => {
    // Simulate a loading delay (e.g., fetching data)
    setTimeout(() => {
      setIsLoading(false); // After 3 seconds, hide the loading modal
    }, 3000);
  }, []);

  return (
    <Router>
      {/* Show the LoadingModal while loading */}
      <LoadingModal isVisible={isLoading} />

      <Routes>
        {/* Public routes (accessible by anyone) */}
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/reset-password" element={<ResetPassword />} />

        {/* Protected routes (only accessible when authenticated) */}
        <Route element={<ProtectedRoute />}>
          <Route path="/" element={<Home />} />
          {/* Add more protected routes below */}
          {/* <Route path="/profile" element={<Profile />} /> */}
          {/* <Route path="/story" element={<StoryPage />} /> */}
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
