import React, { useState, useEffect } from "react";
import { Navigate, Outlet } from "react-router-dom";
import axios from "axios";

const ProtectedRoute = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const verifyToken = async () => {
      try {
        const token = localStorage.getItem("authToken"); // Retrieve the token from localStorage
        if (!token) {
          setIsAuthenticated(false); // No token, not authenticated
          return;
        }

        // Verify token with backend
        const response = await axios.get(
          "http://localhost:3000/api/auth/verify-token",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (response.data.success) {
          setIsAuthenticated(true); // Token is valid
        } else {
          setIsAuthenticated(false); // Token is invalid
        }
      } catch (error) {
        setIsAuthenticated(false); // Error during verification (e.g., expired token)
      } finally {
        setLoading(false); // Stop loading once the token verification is done
      }
    };

    verifyToken();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Loading state while checking token
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" />; // Redirect to login if not authenticated
  }

  return <Outlet />; // Allow access to the protected routes
};

export default ProtectedRoute;
