import React, { useState, useEffect } from "react";
import { Route, Routes, useNavigate, Navigate } from "react-router-dom";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Signup from "./components/Signup";
import Home from "./components/pages/Home";
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Start with null to signify loading
  const navigate = useNavigate();

  // Check token and validate it on app load
  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (!token) {
      setIsAuthenticated(false); // No token, not authenticated
      return;
    }

    const verifyToken = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/auth/verify-token",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`, // Send the token to verify
            },
          }
        );

        const data = await response.json();

        if (response.ok && data.success) {
          setIsAuthenticated(true); // Token is valid, user is authenticated
        } else {
          localStorage.removeItem("authToken"); // Clear token if invalid
          setIsAuthenticated(false); // Not authenticated
        }
      } catch (error) {
        console.error("Token verification failed:", error);
        localStorage.removeItem("authToken"); // In case of error, clear token
        setIsAuthenticated(false); // Not authenticated
      }
    };

    verifyToken();
  }, []);

  // Handle rendering of ProtectedRoute and login redirection based on authentication status
  return (
    <div>
      <Routes>
        {/* Home page (protected route) */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />

        {/* Login page - if user is authenticated, redirect to home */}
        <Route
          path="/login"
          element={
            isAuthenticated === null ? (
              <div>Loading...</div> // Show loading state while checking authentication status
            ) : isAuthenticated ? (
              <Navigate to="/" replace /> // Redirect to home if already authenticated
            ) : (
              <Login />
            )
          }
        />

        {/* Other routes */}
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />

        {/* Fallback Route */}
        <Route
          path="*"
          element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />}
        />
      </Routes>
    </div>
  );
};

export default App;
