import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ForgotPassword from "./components/ForgotPassword";
import ResetPassword from "./components/ResetPassword";
import LoadingModal from "./components/LoadingModal"; // Import LoadingModal component
import Home from "./components/pages/Home";

function App() {
  const [isLoading, setIsLoading] = useState(true); // State to control loading modal
  const [isAuthenticated, setIsAuthenticated] = useState(false); // State to control authentication status

  useEffect(() => {
    // Simulate checking authentication (replace with real auth logic)
    const checkAuth = () => {
      // Here you can check local storage, cookies, or an API to determine if the user is authenticated
      const user = localStorage.getItem("user"); // Example check (replace with your actual logic)
      setIsAuthenticated(!!user); // If there is a "user" in local storage, assume user is authenticated
    };

    checkAuth(); // Check authentication on component mount

    // Simulate a loading delay (e.g., fetching data)
    setTimeout(() => {
      setIsLoading(false); // After 3 seconds, hide the loading modal
    }, 3000);
  }, []);

  return (
    <Router>
      {/* Show the LoadingModal while loading */}
      <LoadingModal isVisible={isLoading} />

      <Routes>
        {/* Redirect to login if the user is not authenticated and tries to access Home */}
        <Route
          path="/"
          element={isAuthenticated ? <Home /> : <Navigate to="/login" />}
        />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/reset-password" element={<ResetPassword />} />
      </Routes>
    </Router>
  );
}

export default App;
