import React, { useState, useEffect } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Signup from "./components/Signup";
import Home from "./components/pages/Home";
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check token and validate it on app load
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (token) {
      verifyToken(token); // Verify token if it exists in localStorage
    } else {
      setIsAuthenticated(false); // No token, not authenticated
    }
  }, []);

  const verifyToken = async (token) => {
    try {
      const response = await fetch(
        "http://localhost:5000/api/auth/verify-token",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`, // Send the token to verify
          },
        }
      );

      const data = await response.json();

      if (response.ok && data.success) {
        setIsAuthenticated(true); // Token is valid, user is authenticated
      } else {
        localStorage.removeItem("authToken"); // Clear token if invalid
        setIsAuthenticated(false); // Not authenticated
      }
    } catch (error) {
      console.error("Token verification failed:", error);
      localStorage.removeItem("authToken"); // In case of error, clear token
      setIsAuthenticated(false); // Not authenticated
    }
  };

  return (
    <div>
      <Routes>
        {/* Home page (protected route) */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />
        {/* Login page */}
        <Route
          path="/login"
          element={isAuthenticated ? <Navigate to="/" replace /> : <Login />} // Properly redirect to "/" if authenticated
        />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
      </Routes>
    </div>
  );
};

export default App;
