import React, { useState, useEffect } from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Signup from "./components/Signup";
import Home from "./components/pages/Home";
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (token) {
      // Verify token with the backend
      verifyToken(token);
    } else {
      setIsAuthenticated(false);
    }
  }, [navigate]);

  const verifyToken = async (token) => {
    try {
      const response = await fetch(
        "http://localhost:5000/api/auth/verify-token",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const data = await response.json();

      if (response.ok && data.success) {
        setIsAuthenticated(true); // Token is valid, user is authenticated
      } else {
        localStorage.removeItem("authToken"); // Invalid token, clear it
        setIsAuthenticated(false);
      }
    } catch (error) {
      console.error("Token verification failed:", error);
      localStorage.removeItem("authToken"); // If the token verification fails, clear it
      setIsAuthenticated(false);
    }
  };

  return (
    <div>
      <Routes>
        {/* Home route protected by authentication check */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />
        {/* Redirect to login if not authenticated */}
        <Route path="/login" element={isAuthenticated ? <Home /> : <Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
      </Routes>
    </div>
  );
};

export default App;
