import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ForgotPassword from "./components/ForgotPassword";
import ResetPassword from "./components/ResetPassword";
import Home from "./components/pages/Home"; // Home page (protected)
import LoadingModal from "./components/LoadingModal"; // Import LoadingModal component
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute component

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Authentication state (null = checking, true = logged in, false = logged out)
  const [isLoading, setIsLoading] = useState(true); // Loading state for showing loading modal

  useEffect(() => {
    // Simulate token validation check
    const token = localStorage.getItem("authToken");

    if (!token) {
      setIsAuthenticated(false);
      setIsLoading(false);
      return;
    }

    // Simulate async token verification
    const verifyToken = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/auth/verify-token",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const data = await response.json();
        if (response.ok && data.success) {
          setIsAuthenticated(true);
        } else {
          localStorage.removeItem("authToken");
          setIsAuthenticated(false);
        }
      } catch (error) {
        console.error("Token verification failed:", error);
        localStorage.removeItem("authToken");
        setIsAuthenticated(false);
      }
      setIsLoading(false); // Stop loading after token verification
    };

    verifyToken();
  }, []);

  // Show loading modal while checking authentication state
  if (isLoading) {
    return <LoadingModal isVisible={isLoading} />;
  }

  return (
    <Router>
      <Routes>
        {/* Login Route */}
        <Route
          path="/login"
          element={isAuthenticated ? <Navigate to="/" replace /> : <Login />}
        />

        {/* Signup Route */}
        <Route path="/signup" element={<Signup />} />

        {/* Forgot Password Route */}
        <Route path="/forgot-password" element={<ForgotPassword />} />

        {/* Reset Password Route */}
        <Route path="/reset-password" element={<ResetPassword />} />

        {/* Protected Home Route */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />

        {/* Redirect to login if route doesn't exist */}
        <Route
          path="*"
          element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
