import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ForgotPassword from "./components/ForgotPassword";
import ResetPassword from "./components/ResetPassword";
import LoadingModal from "./components/LoadingModal"; // Import LoadingModal component
import Home from "./components/pages/Home"; // Import Home page component
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute component

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Authentication state (null = checking, true = logged in, false = logged out)
  const [isLoading, setIsLoading] = useState(true); // State to control loading modal

  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (!token) {
      setIsAuthenticated(false);
      setIsLoading(false);
      return;
    }

    // Simulate an async call to check token validity (e.g., using an API call)
    const verifyToken = async () => {
      try {
        const response = await fetch(
          "http://localhost:5000/api/auth/verify-token",
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`, // Send token to verify
            },
          }
        );

        const data = await response.json();
        if (response.ok && data.success) {
          setIsAuthenticated(true); // Token is valid, user is authenticated
        } else {
          localStorage.removeItem("authToken"); // Clear token if invalid
          setIsAuthenticated(false); // Not authenticated
        }
      } catch (error) {
        console.error("Token verification failed:", error);
        localStorage.removeItem("authToken");
        setIsAuthenticated(false); // Not authenticated
      }
    };

    verifyToken();
  }, []);

  // Render loading modal while checking authentication
  if (isLoading) {
    return <LoadingModal isVisible={isLoading} />;
  }

  return (
    <Router>
      {/* Show the LoadingModal while loading */}
      <LoadingModal isVisible={isLoading} />

      <Routes>
        {/* Login Route */}
        <Route
          path="/login"
          element={isAuthenticated ? <Navigate to="/" replace /> : <Login />}
        />

        {/* Signup Route */}
        <Route path="/signup" element={<Signup />} />

        {/* Forgot Password Route */}
        <Route path="/forgot-password" element={<ForgotPassword />} />

        {/* Reset Password Route */}
        <Route path="/reset-password" element={<ResetPassword />} />

        {/* Protected Home Route - Redirect to login if not authenticated */}
        <Route
          path="/"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <Home />
            </ProtectedRoute>
          }
        />

        {/* Fallback route */}
        <Route
          path="*"
          element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />}
        />
      </Routes>
    </Router>
  );
}

export default App;
